sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   g_form.hideErrorBox('variable');

	var regex = /^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@vipro.com$/;

	newValue = newValue.toString();
	var isValidEmail = regex.test(newValue);
	//alert(isValidEmail);
	if(!isValidEmail){
		//g_form.showErrorBox('email_id','Please enter valid email address','error');
		alert('Email entered is invalid; Enter virpo Email id only');
		g_form.clearValue('email_id');
	}

}
